// Dynmaic Programming function to find the number of times 
// the second string occurs in the first string, 
// whether continuous or discontinuous 

// Number of times a string appears as a subsequence in another string

#include <bits/stdc++.h>

using namespace std;

long count (string a, string b, int m, int n)  { 
    if (dp[m][n] != -1) {
        return dp[m][n];
    }
    if ((m == 0 && n == 0) || n == 0) 
        return dp[m][n]=1; 
    if (m == 0) 
        return dp[m][n]=0; 
    if (a[m - 1] == b[n - 1]) 
        return dp[m][n]=count(a, b, m - 1, n - 1) +  count(a, b, m - 1, n); 
    else
        return dp[m][n]=count(a, b, m - 1, n); 
} 

